INSERT INTO `wp_posts` VALUES('149', '2', '2012-08-30 19:18:19', '2012-08-30 19:18:19', 'Ordering is easy. Follow these steps:\r\n<ol>\r\n	<li>Select the lab tests you would like to order. Click on a category in the left hand column to see a full list of available tests. If you know the name of the test you need, use the search feature to search for test by name.</li>\r\n	<li>Add the selected test(s) to the shopping cart, and continue shopping to select additional tests.</li>\r\n	<li>If you have a discount screening code, enter it at the bottom of the screen.</li>\r\n	<li>Proceed to checkout.</li>\r\n	<li>New users, register as a new customer, and complete your personal information.</li>\r\n	<li>Returning users, enter your user name and password.</li>\r\n	<li>Accept the terms and conditions. (Access the complete terms and conditions in the left hand column.)</li>\r\n	<li>Print your requisition form to bring with you for testing.</li>\r\n	<li>Locate a participating lab near you.</li>\r\n</ol>', 'How do I order a direct access lab test?', '', 'publish', 'open', 'open', '', 'how-do-i-order-a-direct-access-lab-test', '', '', '2012-08-30 19:18:19', '2012-08-30 19:18:19', '', '0', 'http://delica.websitewelcome.com/~sterling/?post_type=question&#038;p=149', '0', 'question', '', '0');
INSERT INTO `wp_posts` VALUES('150', '2', '2012-08-30 19:19:20', '2012-08-30 19:19:20', 'Ordering is easy. Follow these steps:\n<ol>\n	<li>Select the lab tests you would like to order. Click on a category in the left hand column to see a full list of available tests. If you know the name of the test you need, use the search feature to search for test by name.</li>\n	<li>Add the selected test(s) to the shopping cart, and continue shopping to select additional tests.</li>\n	<li>If you have a discount screening code, enter it at the bottom of the screen.</li>\n	<li>Proceed to checkout.</li>\n	<li>New users, register as a new customer, and complete your personal information.</li>\n	<li>Returning users, enter your user name and password.</li>\n	<li>Accept the terms and conditions. (Access the complete terms and conditions in the left hand column.)</li>\n	<li>Print your requisition form to bring with you for testing.</li>\n	<li>Locate a participating lab near you.</li>\n</ol>', 'How do I order a direct access lab test?', '', 'inherit', 'open', 'open', '', '149-autosave', '', '', '2012-08-30 19:19:20', '2012-08-30 19:19:20', '', '149', 'http://delica.websitewelcome.com/~sterling/149-autosave/', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES('151', '2', '2012-08-28 17:08:17', '2012-08-28 17:08:17', '<ul>\r\n	<li>When will I get my results?\r\n<ul>\r\n	<li>Any participant who registers alert levels will be contacted within 36 hours to seek immediate treatment from a physician.  All other participants will learn more about their results through an onsite or telephone review of their results.</li>\r\n</ul>\r\n</li>\r\n	<li>Do I need a doctor’s orders?\r\n<ul>\r\n	<li>Nope! You can simply call Sterling Labs at 1-800-838-0337, or order online. You will be emailed a requisition form that can be printed and taken to any Sterling-contracted lab facility nearest you.</li>\r\n</ul>\r\n</li>\r\n	<li>Will someone be available to help me understand my results?\r\n<ul>\r\n	<li>A certified health coach or nurse will review your results through a telephone or onsite review. A health coach can be contacted during regular business hours Monday-Friday 8am-5pm.</li>\r\n</ul>\r\n</li>\r\n	<li>How do I order a direct access lab test?\r\n<ul>\r\n	<li>Ordering is easy.  Follow these steps:</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<blockquote>\r\n<blockquote>\r\n<ul>\r\n	<li>Select the lab tests you would like to order.  Click on a category in the left hand column to see a full list of available tests.  If you know the name of the test you need, use the search feature to search for test by name.</li>\r\n	<li>Add the selected test(s) to the shopping cart, and continue shopping to select additional tests.</li>\r\n	<li>If you have a discount screening code, enter it at the bottom of the screen.</li>\r\n	<li>Proceed to checkout.</li>\r\n	<li>New users, register as a new customer, and complete your personal information.</li>\r\n	<li>Returning users, enter your user name and password.</li>\r\n	<li>Accept the terms and conditions.  (Access the complete terms and conditions in the left hand column.)</li>\r\n	<li>Print your requisition form to bring with you for testing.</li>\r\n	<li>Locate a participating lab near you.</li>\r\n</ul>\r\n</blockquote>\r\n</blockquote>\r\n<span style=\"color: #ff0000;\">Note:  Change the button to say “Register as a new customer to complete your order.”</span>', 'FAQ Lab Questions', '', 'inherit', 'open', 'open', '', '72-revision-3', '', '', '2012-08-28 17:08:17', '2012-08-28 17:08:17', '', '72', 'http://delica.websitewelcome.com/~sterling/72-revision-3/', '0', 'revision', '', '0');
