INSERT INTO `wp_posts` VALUES('1523', '14', '2013-01-16 17:42:39', '2013-01-16 17:42:39', '<!--[if gte vml 1]><v:shapetype id=\"_x0000_t75\"\r\ncoordsize=\"21600,21600\" o:spt=\"75\" o:preferrelative=\"t\" path=\"m@4@5l@4@11@9@11@9@5xe\"\r\nfilled=\"f\" stroked=\"f\">\r\n<v:stroke joinstyle=\"miter\"/>\r\n<v:formulas>\r\n<v:f eqn=\"if lineDrawn pixelLineWidth 0\"/>\r\n<v:f eqn=\"sum @0 1 0\"/>\r\n<v:f eqn=\"sum 0 0 @1\"/>\r\n<v:f eqn=\"prod @2 1 2\"/>\r\n<v:f eqn=\"prod @3 21600 pixelWidth\"/>\r\n<v:f eqn=\"prod @3 21600 pixelHeight\"/>\r\n<v:f eqn=\"sum @0 0 1\"/>\r\n<v:f eqn=\"prod @6 1 2\"/>\r\n<v:f eqn=\"prod @7 21600 pixelWidth\"/>\r\n<v:f eqn=\"sum @8 21600 0\"/>\r\n<v:f eqn=\"prod @7 21600 pixelHeight\"/>\r\n<v:f eqn=\"sum @10 21600 0\"/>\r\n</v:formulas>\r\n<v:path o:extrusionok=\"f\" gradientshapeok=\"t\" o:connecttype=\"rect\"/>\r\n<o:lock v:ext=\"edit\" aspectratio=\"t\"/>\r\n</v:shapetype><v:shape id=\"_x0000_s1029\" type=\"#_x0000_t75\" alt=\"\" style=\'position:absolute;\r\nmargin-left:0;margin-top:1023.75pt;width:122.25pt;height:78.75pt;z-index:251658240;\r\nmso-wrap-distance-left:3.75pt;mso-wrap-distance-top:3.75pt;\r\nmso-wrap-distance-right:3.75pt;mso-wrap-distance-bottom:3.75pt;\r\nmso-position-horizontal:left;mso-position-horizontal-relative:text;\r\nmso-position-vertical-relative:line\' o:allowoverlap=\"f\">\r\n<v:imagedata src=\"http://ih.constantcontact.com/fs196/1103500019310/img/380.jpg\"/>\r\n<w:wrap type=\"square\"/>\r\n</v:shape><![endif]--><!--[if !vml]--><img src=\"http://ih.constantcontact.com/fs196/1103500019310/img/380.jpg\" alt=\"\" name=\"ACCOUNT.IMAGE.380\" width=\"163\" height=\"105\" align=\"left\" border=\"0\" hspace=\"5\" vspace=\"5\" /><!--[endif]-->Glaucoma is the second leading cause of blindness in the world after diabetes. This eye disease can come in many forms, and all are likely to lead to vision loss and potential blindness. Although babies can be born with glaucoma, it is most common in the elderly and African American populations. Medication and surgery can slow the loss of vision, but taking care of your peepers will keep your eyesight healthy. Wear sunglasses that block UVA and UVB rays, don\'t smoke, and always wear eye protection for sports and other potentially dangerous activities.\r\n\r\nDiagnosing glaucoma is the first step to saving your eyesight. Get a comprehensive dilated eye exam every 2-3 years, or more frequently if you have a family history of cataracts or glaucoma. Visit the <a href=\"http://r20.rs6.net/tn.jsp?e=001qel0SAE1-vIUS5BL17jyzotdACdwIcezhkCurmX1U8nCutoknotdQZySQOTacM8ZDb3yL0k5wAH-EGUjJwwKCcq7yTiS4mb3ocrqoIwDibGneqFdGxKzJQ==\" shape=\"rect\" target=\"_blank\">Glaucoma Research Foundation</a> to assess your risk and learn more about prevention and treatment.', 'Protect your Peepers- Glaucoma Awareness Month', '', 'publish', 'open', 'open', '', 'protect-your-peepers-glaucoma-awareness-month', '', '', '2013-02-06 17:43:46', '2013-02-06 17:43:46', '', '0', 'http://www.sterling-wellness.com/?p=1523', '0', 'post', '', '0');
