INSERT INTO `wp_posts` VALUES('1796', '14', '2013-08-08 20:57:30', '2013-08-08 20:57:30', '<!--[if gte vml 1]><v:shapetype id=\"_x0000_t75\"\r\n coordsize=\"21600,21600\" o:spt=\"75\" o:preferrelative=\"t\" path=\"m@4@5l@4@11@9@11@9@5xe\"\r\n filled=\"f\" stroked=\"f\">\r\n <v:stroke joinstyle=\"miter\"/>\r\n <v:formulas>\r\n  <v:f eqn=\"if lineDrawn pixelLineWidth 0\"/>\r\n  <v:f eqn=\"sum @0 1 0\"/>\r\n  <v:f eqn=\"sum 0 0 @1\"/>\r\n  <v:f eqn=\"prod @2 1 2\"/>\r\n  <v:f eqn=\"prod @3 21600 pixelWidth\"/>\r\n  <v:f eqn=\"prod @3 21600 pixelHeight\"/>\r\n  <v:f eqn=\"sum @0 0 1\"/>\r\n  <v:f eqn=\"prod @6 1 2\"/>\r\n  <v:f eqn=\"prod @7 21600 pixelWidth\"/>\r\n  <v:f eqn=\"sum @8 21600 0\"/>\r\n  <v:f eqn=\"prod @7 21600 pixelHeight\"/>\r\n  <v:f eqn=\"sum @10 21600 0\"/>\r\n </v:formulas>\r\n <v:path o:extrusionok=\"f\" gradientshapeok=\"t\" o:connecttype=\"rect\"/>\r\n <o:lock v:ext=\"edit\" aspectratio=\"t\"/>\r\n</v:shapetype><v:shape id=\"_x0000_s1028\" type=\"#_x0000_t75\" alt=\"\" style=\'position:absolute;\r\n margin-left:0;margin-top:728.25pt;width:144.75pt;height:2in;z-index:251658240;\r\n mso-wrap-distance-left:3.75pt;mso-wrap-distance-top:3.75pt;\r\n mso-wrap-distance-right:3.75pt;mso-wrap-distance-bottom:3.75pt;\r\n mso-position-horizontal:left;mso-position-horizontal-relative:text;\r\n mso-position-vertical-relative:line\' o:allowoverlap=\"f\">\r\n <v:imagedata src=\"http://ih.constantcontact.com/fs196/1103500019310/img/606.jpg\"/>\r\n <w:wrap type=\"square\"/>\r\n</v:shape><![endif]--><!--[if !vml]--><img src=\"http://ih.constantcontact.com/fs196/1103500019310/img/606.jpg\" alt=\"\" name=\"ACCOUNT.IMAGE.606\" width=\"193\" height=\"192\" align=\"left\" border=\"0\" hspace=\"5\" vspace=\"5\" /><!--[endif]-->\r\n\r\n<em>Hit play on one of these food documentaries instead! </em>\r\n\r\nGain behind-the-scenes access into our food supply, health risks, and the politics of food. They say you are what you eat- but what are we eating? Find one of these films on Netflix or iTunes. <em><a href=\"http://r20.rs6.net/tn.jsp?e=001zd3av7FYBkHUZNlzOD0O_HbFWZE21OvtSzRQFSN_Bo-wiVikcfVjqQIQ12k_6d8TFNBCNqepnTJ3NWf3cEof0o-cC98M5hnyE4OxXjgWbYszW6QsMP4L_Toex5PJfyLiXVAbL1h7SWFGesPeAj86blRkpofpJyCmbWpdHp2_1IBtW-hzndsXvmkznhF07t-FImzeNDRkhGA=\" shape=\"rect\" target=\"_blank\">More</a></em>\r\n<ul>\r\n	<li><strong>Farmageddon</strong>- Join the quest to find healthy local food raised by small farmers.</li>\r\n	<li><strong>Hungry for Change</strong>- Uncover the secrets of the diet and weight-loss industry.</li>\r\n	<li><strong>Food, Inc</strong>- Look inside the large scale production of chicken, beef, and field crops.</li>\r\n</ul>\r\n<strong>Forks Over Knives</strong>- Learn how our eating habits affect the rise in heart disease, obesity, and cancer.', 'Skip the New Releases', '', 'inherit', 'open', 'open', '', '1789-revision-2', '', '', '2013-08-08 20:57:30', '2013-08-08 20:57:30', '', '1789', 'http://www.sterling-wellness.com/1789-revision-2/', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES('1798', '14', '2013-05-20 18:12:51', '2013-05-20 18:12:51', 'Sterling Wellness Solutions is looking for the best and brightest individuals to join a dynamic and talented team. Our multi-dimensional approach to wellness requires creative thinking, industry knowledge, and a unified mission of introducing wellness in an exciting new light. Please use the form below to contact us if you are interested in employment with our team.\r\n<h4><span style=\"text-decoration: underline;\"><strong>Current positions available</strong></span>:</h4>\r\nSorry, there are currently no positions available. Please check back later!\r\n\r\n[gravityform id=\"1\" name=\"Careers\" title=\"false\" description=\"false\" ajax=\"true\"]', 'Careers', '', 'inherit', 'open', 'open', '', '8-revision-v1', '', '', '2013-05-20 18:12:51', '2013-05-20 18:12:51', '', '8', 'http://www.sterling-wellness.com/8-revision-9/', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES('1799', '14', '2013-08-26 20:06:19', '2013-08-26 20:06:19', 'Sterling Wellness Solutions is looking for the best and brightest individuals to join a dynamic and talented team. Our multi-dimensional approach to wellness requires creative thinking, industry knowledge, and a unified mission of introducing wellness in an exciting new light. Please use the form below to contact us if you are interested in employment with our team.\r\n<h4><span style=\"text-decoration: underline;\"><strong>Current positions available</strong></span>:</h4>\r\n&nbsp;\r\n\r\n<strong>Program Manager &amp; Health Coach:</strong>\r\n\r\n<em><em>Sterling Wellness Solutions is seeking an enthusiastic and motivated Program Manager &amp; Health Coach. </em>Must be a degreed professional with 3-5 years of experience in health promotions, nursing, dietetics, or an allied health field. Early mornings and significant state-wide travel throughout Louisiana required. Requires excellent presentation skills, interpersonal communication, and computer proficiency. Responsible for engaging and positively impacting individuals and groups, developing and promoting wellness programs, scheduling and facilitating wellness screenings, and providing intensive onsite and telephonic health coaching. Submit resume below.</em>\r\n\r\n[gravityform id=\"1\" name=\"Careers\" title=\"false\" description=\"false\" ajax=\"true\"]', 'Careers', '', 'inherit', 'open', 'open', '', '8-revision-v1', '', '', '2013-08-26 20:06:19', '2013-08-26 20:06:19', '', '8', 'http://www.sterling-wellness.com/8-revision-10/', '0', 'revision', '', '0');
